import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import "./Expense.css";

function ExpenseList({ expenses }) {
  const [filteredDate, setFilteredDate] = useState('2022');

  const saveFilterDateHandler = (filteredDate) => {
    setFilteredDate(filteredDate);
  };

  return (
    <Card className="expenses">
      <ExpenseFilter onSaveFilterDate={saveFilterDateHandler} />
      {expenses.filter(expense => expense.date.includes(filteredDate)).map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
      {/* {expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))} */}
    </Card>
  );
}

export default ExpenseList;
