import React, { useEffect, useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import "./Expense.css";

function ExpenseList({ expenses }) {
  const [filteredDate, setFilteredDate] = useState("2020");

  const saveFilterDateHandler = (filteredDate) => {
    setFilteredDate(filteredDate);
  };

  useEffect(() => {
    console.log(expenses.filter((item) => item.date.indexOf(filteredDate)));
  }, []);

  return (
    <Card className="expenses">
      <ExpenseFilter
        selected={filteredDate}
        onSaveFilterDate={saveFilterDateHandler}
      />
      {expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
      {/* {expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))} */}
    </Card>
  );
}

export default ExpenseList;
